import axios from "axios";

const rolesState = {
  state: {
    showRoleForm: false,
    roles: [],
    rolesToChange: [],
  },
  mutations: {
    setShowRoleForm(state, value) {
      state.showRoleForm = value;
    },

    setRoles(state, roles) {
      state.roles = roles;
    },

    addRole(state, role) {
      if (state.roles.length > 25) {
        state.roles.pop();
        state.roles.push(role);
      } else {
        state.roles.push(role);
      }
    },

    setRolesToChange(state, usersToChange) {
      state.usersToChange = usersToChange;
    },
  },
  actions: {
    toggleRoleForm({ commit }, show) {
      commit("setShowRoleForm", show);
    },

    addRole({ commit }, role) {
      commit("addRole", role);
    },

    async getRoles({ commit, state }) {
      try {
        const response = await axios.get(
          "http://localhost:5000/user/role/role-get",
          {
            params: {
              page: state.page,
            },
            withCredentials: true,
          }
        );
        commit("setRoles", response.data.data);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    },

    setRolesToChange({ commit }, usersToChange) {
      commit("setRolesToChange", usersToChange);
    },

    async deleteRoles({ state }) {
      await axios.post(
        "http://localhost:5000//user/role/roles-delete",
        {
          users: state.rolesToChange,
        },
        {
          withCredentials: true,
        }
      );
    },
  },
  getters: {
    showRoleForm: (state) => state.showRoleForm,
    roles: (state) => state.roles,
    rolesToChange: (state) => state.rolesToChange,
  },
};

export default rolesState;
