import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

const userServiceState = new Vuex.Store({
  state: {
    showUserAddForm: false,
  },
  mutations: {
    toggleUserAddForm(state, show) {
      state.showUserAddForm = show;
    },
  },
  actions: {
    toggleUserAddForm({ commit }) {
      commit("toggleUserAddForm");
    },
  },
  getters: {
    isUserAddFormlVisible: (state) => {
      return state.showUserAddForm;
    },
  },
});

export default userServiceState;
