const userServiceState = {
  state: {
    showUserAddForm: false,
    selectedFilters: [],
  },
  mutations: {
    toggleUserAddForm(state, showForm) {
      state.showUserAddForm = showForm;
    },
    addSelectedFilter(state, { filterName, filterValue }) {
      for (const obj of state.selectedFilters) {
        if (obj.filterName === searchValue) {
          exists = true;
          break;
        }
      }
      state.selectedFilters.push({ filterName, filterValue });
    },
  },
  actions: {
    toggleUserAddForm({ commit }, showForm) {
      commit("toggleUserAddForm", showForm);
    },
    addSelectedFilter({ commit }, { filterName, filterValue }) {
      commit("addSelectedFilter", { filterName, filterValue });
    },
  },
  getters: {
    showUserAddForm: (state) => state.showUserAddForm,
    selectedFilters: (state) => state.selectedFilters,
  },
};

export default userServiceState;
