import axios from "axios";

const rolesState = {
  state: {
    showRoleForm: false,
    roles: [],
    rolesToChange: [],
  },
  mutations: {
    setShowRoleForm(state, value) {
      state.showRoleForm = value;
    },

    setRoles(state, roles) {
      state.roles = roles;
    },

    addRole(state, role) {
      if (state.roles.length > 25) {
        state.roles.pop();
        state.roles.push(role);
      } else {
        state.roles.push(role);
      }
    },
  },
  actions: {
    toggleRoleForm({ commit }, show) {
      commit("setShowRoleForm", show);
    },

    addRole({ commit }, role) {
      commit("addRole", role);
    },

    async getRoles({ commit, state }) {
      try {
        const response = await axios.get(
          "http://localhost:5000/user/role/role-get",
          {
            params: {
              page: state.page,
            },
            withCredentials: true,
          }
        );
        commit("setRoles", response.data.data);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    },
  },
  getters: {
    showRoleForm(state) {
      return state.showRoleForm;
    },

    roles(state) {
      return state.roles;
    },
  },
};

export default rolesState;
