const rolesState = {
  state: {
    showRoleForm: false,
  },
  mutations: {
    setShowRoleForm(state, value) {
      state.showRoleForm = value;
    },
  },
  actions: {
    toggleRoleForm({ commit, state }) {
      const newValue = !state.showRoleForm;
      commit("setShowRoleForm", newValue);
    },
  },
  getters: {
    isRoleFormVisible(state) {
      return state.showRoleForm;
    },
  },
};

export default rolesState;
