const userServiceState = {
  state: {
    showUserAddForm: false,
    selectedFilters: [],
  },
  mutations: {
    toggleUserAddForm(state, showForm) {
      state.showUserAddForm = showForm;
    },
    addSelectedFilter(state, { filterName, filterValue }) {
      let valueChangeObject; // Initialize a variable to hold the object to be updated
      let exists; // Initialize a variable to check if the object already exists
      // Loop through the selectedFilters array to find the object
      for (const filter of state.selectedFilters) {
        if (filter.filterName === filterName) {
          exists = true;
          valueChangeObject = filter;
          break;
        }
      }

      // If the object doesn't exist, add it to the selectedFilters array
      if (!exists) {
        console.log("ASDASD");
        state.selectedFilters.push({ filterName, filterValue });
      }

      // If the object exists and the value needs to be changed
      if (exists && valueChangeObject.filterValue !== filterValue) {
        const indexToDelete = state.selectedFilters.findIndex(
          (filter) => filter.filterName === filterName
        );
        state.selectedFilters.splice(indexToDelete, 1);
        state.selectedFilters.push({ filterName, filterValue });
      }
    },
    removeSelectedFilter(state, filterName) {
      // Use filter method to create a new array without the filter to be removed
      state.selectedFilters = state.selectedFilters.filter(
        (filter) => filter.filterName !== filterName
      );
    },
  },
  actions: {
    toggleUserAddForm({ commit }, showForm) {
      commit("toggleUserAddForm", showForm);
    },
    addSelectedFilter({ commit }, { filterName, filterValue }) {
      commit("addSelectedFilter", { filterName, filterValue });
    },
    removeSelectedFilter({ commit }, filterName) {
      commit("removeSelectedFilter", filterName);
    },
  },
  getters: {
    showUserAddForm: (state) => state.showUserAddForm,
    selectedFilters: (state) => state.selectedFilters,
  },
};

export default userServiceState;
