const activeMenuState = {
  state: {
    activeMenu: "",
  },
  mutations: {
    setActiveMenu(state, menuName) {
      state.activeMenu = menuName;
    },
  },
  actions: {
    setActiveMenu({ commit }, menuName) {
      commit("setActiveMenu", menuName);
    },
  },
  getters: {
    activeMenu: (state) => {
      return state.activeMenu;
    },
  },
};

export default activeMenuState;
